import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Weather.css'; // CSS ÌååÏùºÏùÑ Î∂àÎü¨ÏòµÎãàÎã§.

const Weather = () => {
    const [weather, setWeather] = useState(null);
    const [error, setError] = useState(false);

    const APIKey = `df0044ff4fb762a1c09d857450de6f45`; // Replace with your OpenWeatherMap API key

    useEffect(() => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(position => {
                const { latitude, longitude } = position.coords;
                fetchWeather(latitude, longitude);
            }, () => {
                setError(true);
            });
        } else {
            setError(true);
        }
    }, []);

    const fetchWeather = async (lat, lon) => {
        try {
            const response = await axios.get(`https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&units=metric&lang=kr&appid=${APIKey}`);
            setWeather(response.data);
            setError(false);
        } catch {
            setError(true);
        }
    };

    const getWeatherEmoji = (main) => {
        switch (main.toLowerCase()) {
            case 'clear':
                return '‚òÄÔ∏è';
            case 'rain':
                return 'üåßÔ∏è';
            case 'snow':
                return '‚ùÑÔ∏è';
            case 'clouds':
                return '‚òÅÔ∏è';
            case 'haze':
                return 'üå´Ô∏è';
            default:
                return 'üåà';
        }
    };

    return (
        <div className="weather-container-horizontal">
            {error && (
                <div className="not-found fadeIn">
                    <p>Oops! Unable to fetch weather data :/</p>
                </div>
            )}
            
            {weather && (
                <div className="weather-horizontal">
                    <div className="weather-title">Ïò§ÎäòÏùò ÎÇ†Ïî®</div>
                    <div className="weather-icon">{getWeatherEmoji(weather.weather[0].main)}</div>
                    <div className="weather-info">
                        <p className="location">{weather.name}</p>
                        <p className="temperature">{parseInt(weather.main.temp)}<span>¬∞C</span></p>
                        <p className="description">{weather.weather[0].description}</p>
                        <p className="temp-range">ÏµúÏ†Ä: {parseInt(weather.main.temp_min)}¬∞C / ÏµúÍ≥†: {parseInt(weather.main.temp_max)}¬∞C</p>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Weather;
